const leaderboard = $('Get Current Leaderboard').all();
const movers = $('Get Biggest Movers').all();

const reportStart = new Date(stats.report_start_date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
const reportEnd = new Date(stats.report_end_date).toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' });

// Growth indicators
const playerGrowthEmoji = stats.player_growth_pct > 0 ? 'üìà' : stats.player_growth_pct < 0 ? 'üìâ' : '‚û°Ô∏è';
