// Build top active players table
let activePlayersTable = '';
if (topActive.length > 0) {
  topActive.forEach((player, idx) => {
    const medal = idx === 0 ? 'ü•á' : idx === 1 ? 'ü•à' : idx === 2 ? 'ü•â' : `${idx + 1}.`;
