    leaderboardTable += `
      <tr>
        <td style="padding: 8px; border-bottom: 1px solid #ddd;">${medal}</td>
        <td style="padding: 8px; border-bottom: 1px solid #ddd;">${player.json.display_name} ${activeIcon}</td>
        <td style="padding: 8px; border-bottom: 1px solid #ddd; text-align: right;">${player.json.combined_score.toLocaleString()}</td>
      </tr>
    `;
  });
}

// Build biggest movers section
let moversSection = '';
if (movers.length > 0) {
  let moversTable = '';
  movers.forEach((mover) => {
    moversTable += `
      <tr>
        <td style="padding: 8px; border-bottom: 1px solid #ddd;">${mover.json.display_name}</td>
        <td style="padding: 8px; border-bottom: 1px solid #ddd; text-align: center;">üöÄ +${mover.json.positions_gained}</td>
        <td style="padding: 8px; border-bottom: 1px solid #ddd; text-align: center;">#${mover.json.current_rank}</td>
      </tr>
    `;
  });
  moversSection = `
    <h2>üöÄ Biggest Movers This Week</h2>
    <table>
      <thead>
        <tr>
          <th>Player</th>
          <th style="text-align: center;">Positions Gained</th>
          <th style="text-align: center;">Current Rank</th>
        </tr>
      </thead>
      <tbody>
        ${moversTable}
      </tbody>
    </table>
  `;
}

// Calculate estimated plays per day
const playsPerDay = stats.days_with_activity > 0 ? Math.round(stats.scores_set_this_week / stats.days_with_activity) : 0;

// Revenue estimation (assuming $1 per play)
const estimatedRevenue = stats.scores_set_this_week;

const htmlBody = `
