// Build HTML email from database stats
const stats = $('Get Database Stats').first().json;
const top5 = $('Get Top 5 Players').all();

const reportDate = new Date(stats.report_date).toLocaleDateString('en-US', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

// Build top 5 table
let top5Table = '';
if (top5.length > 0) {
  top5.forEach((player, idx) => {
    const medal = idx === 0 ? 'ü•á' : idx === 1 ? 'ü•à' : idx === 2 ? 'ü•â' : `${player.json.current_rank}.`;
    top5Table += `
      <tr>
        <td style="padding: 8px; border-bottom: 1px solid #ddd;">${medal}</td>
        <td style="padding: 8px; border-bottom: 1px solid #ddd;">${player.json.display_name}</td>
        <td style="padding: 8px; border-bottom: 1px solid #ddd; text-align: right;">${player.json.combined_score.toLocaleString()}</td>
      </tr>
    `;
  });
} else {
  top5Table = '<tr><td colspan="3" style="padding: 8px;">No leaderboard data yet</td></tr>';
}

// Build activity indicator
const activityEmoji = stats.scores_added_today > 0 ? 'üü¢' : '‚ö™';
const activityText = stats.scores_added_today > 0 
  ? `${stats.scores_added_today} new high scores` 
  : 'No new scores';

const htmlBody = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <style>
    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
    .container { max-width: 600px; margin: 0 auto; padding: 20px; }
    .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 20px; border-radius: 8px; text-align: center; }
    .stat-box { background: #f8f9fa; padding: 15px; margin: 10px 0; border-radius: 8px; border-left: 4px solid #667eea; }
    .stat-label { font-size: 12px; color: #666; text-transform: uppercase; }
    .stat-value { font-size: 28px; font-weight: bold; color: #333; }
    table { width: 100%; border-collapse: collapse; margin: 15px 0; }
    th { background: #667eea; color: white; padding: 10px; text-align: left; }
    .footer { text-align: center; color: #999; font-size: 12px; margin-top: 30px; }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>üéÆ Pinball Leaderboard Report</h1>
      <p>${reportDate}</p>
    </div>

    <h2>üìä Database Statistics</h2>

    <div class="stat-box">
      <div class="stat-label">Total High Scores</div>
      <div class="stat-value">${stats.total_scores.toLocaleString()}</div>
    </div>

    <div class="stat-box">
      <div class="stat-label">Registered Players</div>
      <div class="stat-value">${stats.total_players.toLocaleString()}</div>
    </div>

    <div class="stat-box">
      <div class="stat-label">Tracked Machines</div>
      <div class="stat-value">${stats.total_machines}</div>
    </div>

    <div class="stat-box">
      <div class="stat-label">Today's Activity</div>
      <div class="stat-value">${activityEmoji} ${activityText}</div>
      ${stats.active_players_today > 0 ? `<p style="margin: 5px 0 0 0; color: #666;">${stats.active_players_today} active players</p>` : ''}
    </div>

    <h2>üèÜ Current Top 5 - ${stats.current_event || 'Active Event'}</h2>
    <table>
      <thead>
        <tr>
          <th>Rank</th>
          <th>Player</th>
          <th style="text-align: right;">Score</th>
        </tr>
      </thead>
      <tbody>
        ${top5Table}
      </tbody>
    </table>

    <div class="footer">
      <p>Pinball Analytics System | Automated Daily Report</p>
      <p>Data last updated: ${new Date().toLocaleString()}</p>
    </div>
  </div>
</body>
</html>
`;

return [{ 
  json: { 
    subject: `üéÆ Pinball Leaderboard Daily Summary - ${reportDate}`,
    html: htmlBody,
    stats: stats
  } 
}];
